import React from "react";
import Sticky from "../cards/Sticky"
import StaggeredFadeIn from '../animations/StaggeredFadeIn';
import info from '../../data/info.js';
import ARROW from '../../images/arrow_tech.png'
import StickyIcon from "../cards/StickyIcon";
import ReactIcon from "../icons/react.js"; // Import your SVG as a React component
import NodeIcon from "../icons/node_icon.js"; // Import your SVG as a React component



const TextContent = () => {
    return (
        <li className=" flex-1 leading-8 font-normal text-base flex flex-col">
            <p className={`self-start pb-5 last:pb-0 basis-0 max-md:max-w-full `}>
                If there's one sentance you'd use to describe me it might be: <span className="font-bold">I like to keep my hands busy.</span>
            </p>
            <p className={`self-start pb-5 last:pb-0 basis-0 max-md:max-w-full `}>
                Hi! I'm Katherine Ye, a Computer Engineering student passionate about design and technology. I'm interested in the intersection of creativity and innovation, exploring the application of technology in our lives and other fields. As I continue my journey in this ever-evolving industry,  <span className="font-bold">I'm excited to keep my hands busy — learning, experimenting, and contributing to the future of technology</span>.
            </p> 
            <p className={`self-start pb-5 last:pb-0 basis-0 max-md:max-w-full `}>
                When I'm not at the computer you can find me drawing, cooking, on a hike, or playing the NYT crossword archives.
            </p> 
        </li> 
    );
}

const ImagePlaceholder = () => {
    return (
        <div className="z-1 max-w-[40%] flex overflow-visible flex-col border-zinc-200 border bg-white px-4 pt-4 pb-4 flex-1 max-md:max-w-full transition ease-in-out group-hover:shadow-xl">
            <div className="border-zinc-200 border w-full min-h-[185px] h-full bg-fuchsia-100 max-md:max-w-full">
            </div>
        </div>
    );
}

function AboutMe() {
    const paragraphs = [
      "Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. when an unknown printer took a galley of type and scrambled it to make a type specimen book.", 
      "Outside of work you can find me drawing or painting, cooking, on a hike, at the tennis court, or playing the NYT crossword archives."
    ];

    return (
        <div className="flex flex-col text-left px-[20%] pt-2 pb-10">
            <div className="flex flex-col gap-8 justify-center w-full text-black max-md:max-w-full ">
                <h1 className="text-5xl font-['Myfont'] max-md:max-w-full">
                    ABOUT ME
                </h1>
                <div className="flex flex-col">
                    <div className="flex w-full flex-row gap-x-10 mb-12">
                        <TextContent contents={paragraphs} />
                        <ImagePlaceholder />
                    </div> 
                    <div className="flex flex-row justify-center">

                        <div className="">
                            <StaggeredFadeIn classNames={'flex flex-row gap-1.5'}>
                                <StickyIcon colour="#9ddcff" shadow="#82CBF3" angle="5deg" text="Hello this is a sticky" Icon={ReactIcon} iconSize={30} />
                                <StickyIcon colour="#ffc9f0" shadow="#EEA8DA" angle="-5deg" text="Hello this is a sticky" Icon={NodeIcon} iconSize={30} />
                                <StickyIcon colour="#FFE68C" shadow="#F5D972" angle="5deg" text="Hello this is a sticky" Icon={PythonIcon} iconSize={30}  />
                            </StaggeredFadeIn>
                        </div>
                        <div className="flex w-fit">
                                <img src={ARROW} className=" -translate-y-4 object-scale-down max-w-52"/>
                        </div>
                    </div>

                </div>

            </div>
            {/*
            <article className="flex flex-col justify-center w-full text-black max-md:max-w-full">
                <h1 className="text-2xl font-semibold leading-none max-md:max-w-full">
                # about_me
                </h1>
                <div className="flex flex-wrap gap-10 mt-10 w-full text-base max-md:max-w-full">
                <TextContent content={paragraphs[0]} />
                <ImagePlaceholder />
                </div>
            </article>
            */}

        </div>
    );
}

export default AboutMe;
