import React from 'react';
import IconExternal from '../icons/external';
import IconExternalOpen from '../icons/externalopen';

const ProjectCard = ({ title, description, technologies, imagePosition, external}) => {

  return (
    <div className="flex overflow-hidden ">

        <div className="flex overflow-hidden flex-wrap flex-1 shrink gap-10 w-full basis-0 min-w-[240px] max-md:max-w-full">
            {imagePosition === 'left' ? (
                <>
                    <div className="flex overflow-hidden flex-col border-zinc-200 border bg-white px-4 pt-4 pb-6 flex-1 shrink basis-0 min-w-[240px] max-md:max-w-full">
                        <div className="flex flex-1 w-full bg-fuchsia-100 min-h-[185px] max-md:max-w-full" />
                    </div>
                    <ProjectDetails title={title} description={description} technologies={technologies} external={external}/>
                </>
            ) : (
                <>
                    <ProjectDetails title={title} description={description} technologies={technologies} external={external}/>
                    <div className="flex overflow-hidden flex-col flex-1 shrink px-5 basis-0 min-w-[240px] max-md:max-w-full">
                        <div className="flex flex-1 w-full bg-fuchsia-100 min-h-[185px] max-md:max-w-full" />
                    </div>
                </>
            )}
        </div>
    </div>
  );
};

const ProjectDetails = ({ title, description, technologies, external}) => {
    return (
        <div className="hover:shadow-lg flex overflow-hidden flex-col flex-1 border-zinc-200 border rounded pt-4 pb-6 shrink group text-left my-auto text-sm basis-0 min-w-[240px] max-md:max-w-full">
            <h3 className="px-6 text-base inline-flex leading-6 items-baseline w-fit text-black font-bold max-md:max-w-fit relative absolute h-3 w-full ">
                <a href={external} target="_blank" rel="noopener noreferrer" className="inline-flex gap-2 group items-center ">
                    {title}
                    <span className="transition-transform group-hover:-translate-y-1 group-hover:translate-x-1 group-focus-visible:-translate-y-1 group-focus-visible:translate-x-1 motion-reduce:transition-none translate-y-px">
                    <IconExternalOpen/>
                  </span>
                </a>
            </h3>
            <hr class="h-px my-6 bg-red-200 border-0 dark:bg-gray-700"></hr>
            <p className="px-6 text-sm leading-6 text-zinc-500 max-md:max-w-full">{description}</p>
            <div className="px-6 flex overflow-hidden flex-wrap gap-2.5 items-start mt-3.5 w-full whitespace-nowrap max-md:max-w-full">
                {technologies.map((tech, index) => (
                    <span key={index} className="flex overflow-hidden gap-2.5 justify-center items-center px-4 py-1 text-xs font-medium bg-zinc-100 rounded-[30px]">
                        <span className="self-stretch my-auto bg-clip-text">{tech}</span>
                    </span>
                ))}
            </div>
        </div>
    );
};

export default ProjectCard;
