import React from 'react';
import { useState } from 'react';
import { useRef } from 'react';
import FadeInFromBottom from '../animations/FadeInFromBottom';
import AVATAR from '../../images/avatar.PNG';
import HOVER_PLAY from '../../images/hover_play.png';
import HELLO_THERE from '../../images/hello_there.mp4'

const Stickies = () => {
    const [isHovered, setIsHovered] = useState(false);
    const videoRef = useRef(null);
    const [isPeeled, setIsPeeled] = useState(false);


    const handleMouseEnter = () => {
        setIsPeeled(true);
        setIsHovered(true);
        if (videoRef.current) {
            videoRef.current.currentTime = 0; // Reset the video to the beginning
            videoRef.current.play();
        }
    };

    const handleMouseLeave = () => {
        setIsPeeled(false);
        setIsHovered(false);
        if (videoRef.current) {
            videoRef.current.pause();
        }
    };

    return(
        <div className="shrink-1 flex flex-col gap-10">
            <FadeInFromBottom>
                <div 

                    className="relative shrink-1 z-10 rotate-[4deg]"
                    onMouseEnter={handleMouseEnter}
                    onMouseLeave={handleMouseLeave}
                >
                    <div className="w-72 h-10 bg-[#82CBF3]"></div>
                    <div className="relative flex flex-1 w-72 h-[200px]">
                        {/* Underlying Content */}     
                        <div className="w-full h-full text-white flex items-center justify-center">
                        <video
                            ref={videoRef}
                            src={HELLO_THERE} // Replace with your video URL
                            className="absolute top-0 left-0 w-full h-full object-cover"
                            loop

                        />
                        </div>
                        {/* Peeling Layer */}
                        <div className="absolute top-0 left-0 font-['Myfont'] text-2xl h-full w-full">
                            <div className="flex place-content-center pt-4  h-full w-full drop-shadow-lg bg-[#9ddcff] "
                                style={{
                                    transform: isPeeled ? 'rotateX(85deg)' : 'none',
                                    transformOrigin: 'top center',
                                    transition: 'transform 0.7s ease-in-out, box-shadow 0.7s ease-in-out',
                                    boxShadow: isPeeled ? '0 60px 60px rgba(0, 0, 0, 0.3)' : 'none',
                                }}
                            >
                                <img className="object-scale-down h-full w-full"  src={AVATAR} />
                            </div>
                        </div>

                    </div>
                </div>
                <div className="relative translate-x-20 -translate-y-2 z-0 font-['Myfont'] text-2xl w-full rotate-[-2deg]">
                    <div className="w-64 h-10 bg-[#EEA8DA]"></div>
                    <div className="flex flex-row justify-start pt-2 pb-5 w-64 h-28 drop-shadow-lg bg-[#ffc9f0]">
                        <img className="object-contain w-full h-full"  src={HOVER_PLAY} />
                    </div>
                </div>
            </FadeInFromBottom>

        </div>
    );
}

const Profile = () => {

    return (
        <div className="flex flex-col shrink-0 h-full w-fit text-left">
                {/*
                {target && <MouseTracker offset={{ x: 1, y: 1 }}>
                        <iframe width="280" height="158" src="https://www.youtube.com/embed/rEq1Z0bjdwc?autoplay=1&start=8" allowfullscreen>
                        </iframe>
                    </MouseTracker>} */}
                <div className="m-auto pb-20 flex flex-col w-full gap-3">
                    <p className="text-7xl mb-8 font-['Inter-ExtraBold'] max-md:max-w-full max-md:text-4xl ">
                        Hi, my name is
                    </p>
                    <p className="text-7xl font-['Inter-ExtraBold'] relative inline-flex items-end w-fit bg-[#FFE68C] absolute h-10 z-[-1] bottom-0 max-md:max-w-full max-md:text-4xl ">
                        Katherine Ye
                    </p>
                    <p className=" w-64 max-w-64 text-justify mt-8 text-xl leading-8 font-['Inter-SemiBold] text-[color:var(--sds-color-text-default-default)] max-md:mt-10 max-md:max-w-80">
                        I'm a <span className="underline underline-offset-4 decoration-2 decoration-[#F384D4] ">designer and developer studying Computer Engineering at the University of Toronto</span>. I love building things and helping people!
                    </p>
                </div>

                {/*
                <div className="flex items-start mt-0">
                    <div className="flex z-10 shrink-0 mr-0 max-w-full bg-yellow-400 bg-opacity-50 h-[18px] w-[378px]"/>
                    <div className="flex z-10 shrink-0 mt-8 mr-0 max-w-full bg-yellow-400 bg-opacity-50 h-[18px] w-[392px]"/>
                </div>
                */}
        </div>

    );
};

const Hero = () => {
  return (
    <div className='flex justify-between flex-row h-fit gap-6 columns-2 w-full max-md:px-5'>
        <Profile />
        <Stickies />
    </div>
  );
};

export default Hero;
