import React from 'react';
import { useState } from 'react';
import { useRef } from 'react';
import FadeInFromBottom from '../animations/FadeInFromBottom';

const Stickies = () => {
    const [isHovered, setIsHovered] = useState(false);

    const [isPeeled, setIsPeeled] = useState(false);

    const handleMouseEnter = () => {
        setIsPeeled(true);
        setIsHovered(true);
    };

    const handleMouseLeave = () => {
        setIsPeeled(false);
        setIsHovered(false);
    };

    return(
        <div className="shrink  pb-20 flex flex-col justify-center gap-10">
            <div className="relative">

            </div>

        </div>
    );
}

const Profile = () => {

    return (
        <div className="flex flex-col shrink-0 h-full w-[50vw] text-left">
                {/*
                {target && <MouseTracker offset={{ x: 1, y: 1 }}>
                        <iframe width="280" height="158" src="https://www.youtube.com/embed/rEq1Z0bjdwc?autoplay=1&start=8" allowfullscreen>
                        </iframe>
                    </MouseTracker>} */}
                <div className="m-auto pb-20 flex flex-col w-full gap-3">
                    <p className="text-7xl mb-6 font-['Inter-ExtraBold'] max-md:max-w-full max-md:text-4xl ">
                        Hi, my name is
                    </p>
                    <p className="text-7xl font-['Inter-ExtraBold'] relative inline-flex items-end w-fit bg-[#FFE68C] absolute h-12 z-[-1] bottom-0 max-md:max-w-full max-md:text-4xl ">
                        Katherine Ye
                    </p>
                    <p className=" max-w-[70%] text-justify mt-10 text-xl leading-8 font-normal text-[color:var(--sds-color-text-default-default)] max-md:mt-10 max-md:max-w-[70%]">
                        I'm a <span className="underline underline-offset-4 decoration-2 decoration-[#F384D4] ">designer and developer studying Computer Engineering at the University of Toronto</span>, dedicated to building innovative solutions and continuously exploring the ever-growing field of technology.
                    </p>
                </div>

                {/*
                <div className="flex items-start mt-0">
                    <div className="flex z-10 shrink-0 mr-0 max-w-full bg-yellow-400 bg-opacity-50 h-[18px] w-[378px]"/>
                    <div className="flex z-10 shrink-0 mt-8 mr-0 max-w-full bg-yellow-400 bg-opacity-50 h-[18px] w-[392px]"/>
                </div>
                */}
        </div>

    );
};

const Hero = () => {
  return (
    <div className='flex flex-row h-[100vh] columns-2 px-48 max-md:px-5'>
        <Profile />
        <Stickies />
    </div>
  );
};

export default Hero;
