import React, { useEffect, useState } from "react";
import {
  Box,
  Stack,
  Image,
  Text,
  Link,
  Spinner,
} from "@chakra-ui/react";
import styled from "styled-components";
import getNowPlayingItem from "../../lib/SpotifyAPI.js";
import SpotifyLogo from "./SpotifyLogo";
import PlayingAnimation from "./PlayingAnimation";
import "../../styles/SpotifyPlayer.css";

const SpotifyNowPlaying = (props) => {
  const [result, setResult] = useState({});

  useEffect(() => {
    Promise.all([
      getNowPlayingItem(
        props.client_id,
        props.client_secret,
        props.refresh_token
      ),
    ]).then((results) => {
      setResult(results[0]);
    });
  }, []);

  return result.isPlaying ? (
    <div className="nowplayingcard">
      <div className="nowplayingcontainer-inner">
        <img id="trackart" src={result.albumImageUrl}></img>
        <div className="trackInfo">
          <a id="tracktitle">{result.title}</a>
          <a href="#" id="trackartist">
            {result.artist}
          </a>
        </div>
      </div>
    </div>
  ) : (
    <div>Not playing</div>
  );
};

export default SpotifyNowPlaying;

const Center = styled.div`
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
`;
