import React from 'react';
import { useState } from 'react';
import { useRef } from 'react';
import FadeInFromBottom from '../animations/FadeInFromBottom';
import AVATAR from '../../images/avatar.PNG';

const Stickies = () => {
    const [isHovered, setIsHovered] = useState(false);

    const [isPeeled, setIsPeeled] = useState(false);

    const handleMouseEnter = () => {
        setIsPeeled(true);
        setIsHovered(true);
    };

    const handleMouseLeave = () => {
        setIsPeeled(false);
        setIsHovered(false);
    };

    return(
        <div className="shrink flex flex-col justify-center gap-10">
            <FadeInFromBottom>
                <div 
                    className="relative z-10 rotate-2"
                    onMouseEnter={handleMouseEnter}
                    onMouseLeave={handleMouseLeave}
                >
                    <div className="w-full h-10 bg-[#82CBF3]"></div>
                    <div className="relative w-full h-fit">
                        {/* Underlying Content */}     
                        <div className="w-full h-full text-white flex items-center justify-center">
                            <iframe
                            className="w-full h-full"
                            src={isHovered ? `https://www.youtube.com/embed/rEq1Z0bjdwc?autoplay=1&start=8` : `https://www.youtube.com/embed/rEq1Z0bjdwc`}
                            title="YouTube video player"
                            frameBorder="0"
                            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                            allowFullScreen
                            ></iframe>
                        </div>
                        {/* Peeling Layer */}
                        <div className="absolute top-0 left-0 font-['Myfont'] text-2xl h-full w-full">
                            <div className="flex place-content-center pt-4 w-full h-full drop-shadow-lg bg-[#FFE68C] "
                                style={{
                                    transform: isPeeled ? 'rotateX(85deg)' : 'none',
                                    transformOrigin: 'top center',
                                    transition: 'transform 0.7s ease-in-out, box-shadow 0.7s ease-in-out',
                                    boxShadow: isPeeled ? '0 60px 60px rgba(0, 0, 0, 0.3)' : 'none',
                                }}
                            >
                                <img className="" src={AVATAR} />
                            </div>
                        </div>

                    </div>
                </div>
                <div className="relative translate-x-20 z-0 font-['Myfont'] text-2xl w-full rotate-[-2deg]">
                    <div className="w-full h-10 bg-[#F5D972]"></div>
                    <div className="px-5 py-5 w-full min-h-36 drop-shadow-lg bg-[#9ddcff]">
                        <h1>This is me!</h1>
                        <p>Hello this is my website</p>
                    </div>
                </div>
            </FadeInFromBottom>

        </div>
    );
}

const Profile = () => {

    return (
        <div className="flex flex-col shrink-0 h-full w-fit text-left">
                {/*
                {target && <MouseTracker offset={{ x: 1, y: 1 }}>
                        <iframe width="280" height="158" src="https://www.youtube.com/embed/rEq1Z0bjdwc?autoplay=1&start=8" allowfullscreen>
                        </iframe>
                    </MouseTracker>} */}
                <div className="m-auto pb-20 flex flex-col w-full gap-3">
                    <p className="text-7xl mb-8 font-['Inter-ExtraBold'] max-md:max-w-full max-md:text-4xl ">
                        Hi, my name is
                    </p>
                    <p className="text-7xl font-['Inter-ExtraBold'] relative inline-flex items-end w-fit bg-[#FFE68C] absolute h-10 z-[-1] bottom-0 max-md:max-w-full max-md:text-4xl ">
                        Katherine Ye
                    </p>
                    <p className=" max-w-[30vw] text-justify mt-8 text-xl leading-8 font-['Inter-SemiBold] text-[color:var(--sds-color-text-default-default)] max-md:mt-10 max-md:max-w-[70%]">
                        I'm a <span className="underline underline-offset-4 decoration-2 decoration-[#F384D4] ">designer and developer studying Computer Engineering at the University of Toronto</span>, dedicated to building innovative solutions and continuously exploring the ever-growing field of technology.
                    </p>
                </div>

                {/*
                <div className="flex items-start mt-0">
                    <div className="flex z-10 shrink-0 mr-0 max-w-full bg-yellow-400 bg-opacity-50 h-[18px] w-[378px]"/>
                    <div className="flex z-10 shrink-0 mt-8 mr-0 max-w-full bg-yellow-400 bg-opacity-50 h-[18px] w-[392px]"/>
                </div>
                */}
        </div>

    );
};

const Hero = () => {
  return (
    <div className='flex gap-20 flex-row h-fit pt-40 columns-2 px-80 max-md:px-5'>
        <Profile />
        <Stickies />
    </div>
  );
};

export default Hero;
