import React from 'react';
import { useState } from 'react';
import { useRef } from 'react';
import { useEffect } from 'react';

function Profile() {
    const [target, setTarget] = useState(null);
    const iframeRef = useRef(null);
    const playerRef = useRef(null);
    const [position, setPosition] = useState({ x: 0, y: 0 });

    const [isPeeled, setIsPeeled] = useState(false);

    useEffect(() => {
        // Load the YouTube IFrame Player API script
        const tag = document.createElement('script');
        tag.src = 'https://www.youtube.com/iframe_api';
        const firstScriptTag = document.getElementsByTagName('script')[0];
        firstScriptTag.parentNode.insertBefore(tag, firstScriptTag);

        // This function is called by the API when it's ready
        window.onYouTubeIframeAPIReady = () => {
            playerRef.current = new window.YT.Player(iframeRef.current, {
                events: {
                    onReady: onPlayerReady,
                },
            });
        };

        function onPlayerReady(event) {
          event.target.pauseVideo(); // Ensure the video is paused initially
        }
    }, []);

    const handleMouseEnter = () => {
        setIsPeeled(true);
        if (playerRef.current) {
            playerRef.current.playVideo();
        }

    };

    const handleMouseLeave = () => {
        setIsPeeled(false);
        if (playerRef.current) {
            playerRef.current.pauseVideo();
        }
    };


    return (
        <div className="flex overflow-hidden flex-col justify-center px-24 py-60 w-full max-md:px-5 w-full text-left">
            <div className="flex flex-col text-left items-start gap-3 pt-3.5 pr-16 pb-16  max-w-full w-fit max-md:pr-5">
                {/*
                {target && <MouseTracker offset={{ x: 1, y: 1 }}>
                        <iframe width="280" height="158" src="https://www.youtube.com/embed/rEq1Z0bjdwc?autoplay=1&start=8" allowfullscreen>
                        </iframe>
                    </MouseTracker>} */}
                <div className="relative inline-block">
                    <h1 className="cursor-pointer text-4xl font-['Inter-ExtraBold'] text-black max-md:max-w-full" onMouseEnter={handleMouseEnter}
                    onMouseLeave={handleMouseLeave}>
                    Hello there!
                    </h1>

                </div>

                <p className="text-7xl font-['Inter-ExtraBold'] leading max-md:max-w-full max-md:text-4xl ">
                    I'm&nbsp;
                    <span className="relative inline-flex items-baseline group w-fit before:content-[''] before:bg-[#FFE68C] before:absolute before:w-[0%] before:h-10 before:z-[-1] before:transition-all before:duration-[0.3s] before:ease-[ease-in-out] before:left-0 before:bottom-0 hover:before:w-full hover:before:bottom-0">
                        Katherine Ye
                    </span>
                </p>
                <p className="w-[50%] mt-5 text-xl leading-8 font-normal text-[color:var(--sds-color-text-default-default)] max-md:mt-10 max-md:max-w-[70%]">
                    I'm a <span className="underline underline-offset-4 decoration-2 decoration-[#F384D4] ">designer and developer studying Computer Engineering at the University of Toronto</span>, dedicated to building innovative solutions and continuously exploring the ever-growing field of technology.
                </p>
                {/*
                <div className="flex items-start mt-0">
                    <div className="flex z-10 shrink-0 mr-0 max-w-full bg-yellow-400 bg-opacity-50 h-[18px] w-[378px]"/>
                    <div className="flex z-10 shrink-0 mt-8 mr-0 max-w-full bg-yellow-400 bg-opacity-50 h-[18px] w-[392px]"/>
                </div>
                */}
            </div>
            <div className="relative w-64 h-64">
                {/* Underlying Content */}     
                <div className="w-full h-full bg-blue-500 text-white flex items-center justify-center">
                    <div className="absolute top-0 left-0 w-full h-full">
                        <iframe
                        ref={iframeRef}
                        id="ytplayer"
                        type="text/html"
                        width="100%"
                        height="100%"
                        src="https://www.youtube.com/embed/rEq1Z0bjdwc?autoplay=1&start=8"
                        frameBorder="0"
                        className="object-cover"
                        ></iframe>
                    </div>
                </div>

                {/* Peeling Layer */}
                <div
                    className="absolute top-0 left-0 w-full h-full bg-gray-800 text-white flex items-center justify-center"
                    style={{
                    transform: isPeeled ? 'rotateX(90deg) translateY(-100%)' : 'none',
                    transformOrigin: 'top center',
                    transition: 'transform 0.7s ease-in-out, box-shadow 0.7s ease-in-out',
                    boxShadow: isPeeled ? '0 20px 20px rgba(0, 0, 0, 0.3)' : 'none',
                    }}
                >
                    <h1>Peel Me</h1>
                </div>
            </div>
        </div>

    );
};

const Hero = () => {
  return (
    <div className="flex overflow-hidden flex-col h-dvh">
      <Profile />
    </div>
  );
};

export default Hero;
