import React from 'react';
import { useState } from 'react';
import { useRef } from 'react';
import Sticky from "../cards/Sticky"

function Profile() {
    const [target, setTarget] = useState(null);
    const [isHovered, setIsHovered] = useState(false);
    const [position, setPosition] = useState({ x: 0, y: 0 });

    const [isPeeled, setIsPeeled] = useState(false);

    const handleMouseEnter = () => {
        setIsPeeled(true);
        setIsHovered(true);

    };

    const handleMouseLeave = () => {
        setIsPeeled(false);
        setIsHovered(false);
    };


    return (
        <div className="flex flex-row px-24 py-60 w-full max-md:px-5 w-full text-left">
            <div className="flex overflow-hidden flex-col justify-center text-left ">
                <div className="flex flex-col text-left items-start gap-3 pt-3.5 pb-16  max-w-full w-fit max-md:pr-5">
                    {/*
                    {target && <MouseTracker offset={{ x: 1, y: 1 }}>
                            <iframe width="280" height="158" src="https://www.youtube.com/embed/rEq1Z0bjdwc?autoplay=1&start=8" allowfullscreen>
                            </iframe>
                        </MouseTracker>} */}
                    <div className="relative inline-block">
                        <h1 className="cursor-pointer text-4xl font-['Inter-ExtraBold'] text-black max-md:max-w-full" >
                        Hello there!
                        </h1>

                    </div>

                    <p className="text-7xl font-['Inter-ExtraBold'] leading max-md:max-w-full max-md:text-4xl ">
                    Hi, my name is&nbsp;
                        <span className="px-2 relative inline-flex items-end w-fit bg-[#FFE68C] absolute h-12 z-[-1] bottom-0">
                            Katherine Ye
                        </span>
                    </p>
                    <p className="w-[75%] mt-5 text-xl leading-8 font-normal text-[color:var(--sds-color-text-default-default)] max-md:mt-10 max-md:max-w-[70%]">
                        I'm a <span className="underline underline-offset-4 decoration-2 decoration-[#F384D4] ">designer and developer studying Computer Engineering at the University of Toronto</span>, dedicated to building innovative solutions and continuously exploring the ever-growing field of technology.
                    </p>
                    {/*
                    <div className="flex items-start mt-0">
                        <div className="flex z-10 shrink-0 mr-0 max-w-full bg-yellow-400 bg-opacity-50 h-[18px] w-[378px]"/>
                        <div className="flex z-10 shrink-0 mt-8 mr-0 max-w-full bg-yellow-400 bg-opacity-50 h-[18px] w-[392px]"/>
                    </div>
                    */}
                </div>
            </div>
            <div className="flex flex-col justify-center">
                <div 
                    className="w-72 h-72"
                    onMouseEnter={handleMouseEnter}
                    onMouseLeave={handleMouseLeave}
                >
                    <div className="w-full h-10 bg-[#82CBF3]"></div>
                    <div className="relative w-full h-full">
                        {/* Underlying Content */}     
                        <div className="w-full h-full text-white flex items-center justify-center">
                            <iframe
                            className="w-full max-w-md h-full"
                            src={isHovered ? `https://www.youtube.com/embed/rEq1Z0bjdwc?autoplay=1&start=8` : `https://www.youtube.com/embed/rEq1Z0bjdwc`}
                            title="YouTube video player"
                            frameBorder="0"
                            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                            allowFullScreen
                            ></iframe>
                        </div>
                        {/* Peeling Layer */}
                        <div className="absolute top-0 left-0 font-['Myfont'] text-2xl h-full w-full">
                            <div className="px-5 py-5 w-full h-full drop-shadow-lg bg-[#9ddcff]"
                                style={{
                                    transform: isPeeled ? 'rotateX(85deg)' : 'none',
                                    transformOrigin: 'top center',
                                    transition: 'transform 0.7s ease-in-out, box-shadow 0.7s ease-in-out',
                                    boxShadow: isPeeled ? '0 20px 20px rgba(0, 0, 0, 0.3)' : 'none',
                                }}

                            >
                                <p>Hello</p>
                            </div>
                        </div>

                    </div>
                </div>
            </div>
        </div>
    );
};

const Hero = () => {
  return (
    <div className="flex overflow-hidden flex-col h-dvh">
      <Profile />
    </div>
  );
};

export default Hero;
