import React, { useState, useEffect } from 'react';

const navLinks = [
    { title: 'About', url: '/about' },
    { title: 'Experience', url: '/experience' },
    { title: 'Projects', url: '/projects' }
];

const bgColor = 'bg-gray-800';
const modalColor = 'bg-gray-900';

const Navbar = () => {
    const [isMobile, setIsMobile] = useState(window.innerWidth < 769);
    const [showModal, setShowModal] = useState(false);

    useEffect(() => {
        const handleResize = () => {
            setIsMobile(window.innerWidth < 769);
        };

        window.addEventListener('resize', handleResize);

        return () => {
            window.removeEventListener('resize', handleResize);
        };
    }, []);

    const toggleModal = () => {
        setShowModal(!showModal);
    };

    const handleBarsIconClick = () => {
        toggleModal();
    };

    return (
        <>
                <div className="flex justify-between w-full items-center py-4 ">
                    <div className="text-black font-['Inter-ExtraBold'] text-xl">Logo</div>
                    <ul className="flex gap-8 md:gap-16 items-center justify-center text-center cursor-pointer">
                        {navLinks.map((link, index) => (
                            <li key={index} className="text-black text-sm">{link.title}</li>
                        ))}
                    </ul>
                </div>

        </>
    );
}

export default Navbar;
