import React from "react";
import Sticky from "../cards/Sticky"
import StaggeredFadeIn from '../animations/StaggeredFadeIn';

const TextContent = ({ contents, additionalClasses = '' }) => {
    return (
        <li className="flex-1 leading-6 font-medium text-base shrink flex flex-col max-md:max-w-full">
            {contents.map((content, index) => (
                <p key={index} className={`self-start pb-5 last:pb-0 basis-0 max-md:max-w-full ${additionalClasses}`}>
                    {content}
                </p>
            ))}
        </li> 
    );
}

const ImagePlaceholder = () => {
    return (
        <div className="flex overflow-hidden flex-col flex-1 shrink px-5 basis-0 max-md:max-w-full">
            <div className="flex flex-1 w-full bg-fuchsia-100 max-md:max-w-full" />
        </div>
    );
}

function AboutMe() {
    const paragraphs = [
      "Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. when an unknown printer took a galley of type and scrambled it to make a type specimen book.", 
      "Outside of work you can find me drawing or painting, cooking, on a hike, at the tennis court, or playing the NYT crossword archives."
    ];

    return (
        <div className="flex flex-col text-left px-40 py-20">
            <div className="flex flex-col gap-10 justify-center w-full text-black max-md:max-w-full pr-20">
                <h1 className="text-5xl font-['Myfont'] max-md:max-w-full">
                    ABOUT ME
                </h1>
                <div className="flex flex-col">
                    <div className="flex flex-row grid-rows-2 gap-x-20 mb-20">
                        <TextContent contents={paragraphs} />
                        <ImagePlaceholder />
                    </div> 
                    <div className="flex flex-row gap-1.5">
                        <StaggeredFadeIn classNames={[]}>
                            <Sticky colour="#9ddcff" shadow="#82CBF3" angle="5deg" text="Hello this is a sticky" title="Python" />
                            <Sticky colour="#ffc9f0" shadow="#EEA8DA" angle="-5deg" text="Hello this is a sticky" title="PYTHON"/>
                            <Sticky colour="#FFE68C" shadow="#F5D972" angle="5deg" text="Hello this is a sticky" title="REACT" />
                        </StaggeredFadeIn>
                    </div>   
                </div>

            </div>
            {/*
            <article className="flex flex-col justify-center w-full text-black max-md:max-w-full">
                <h1 className="text-2xl font-semibold leading-none max-md:max-w-full">
                # about_me
                </h1>
                <div className="flex flex-wrap gap-10 mt-10 w-full text-base max-md:max-w-full">
                <TextContent content={paragraphs[0]} />
                <ImagePlaceholder />
                </div>
            </article>
            */}

        </div>
    );
}

export default AboutMe;
