import  { ReactNode } from "react";
import React, {
  createContext,
  forwardRef,
  useCallback,
  useEffect,
  useImperativeHandle,
  useMemo,
  useRef,
} from "react";
import confetti from "canvas-confetti";
import  {
  GlobalOptions as ConfettiGlobalOptions,
  CreateTypes as ConfettiInstance,
  Options as ConfettiOptions,
} from "canvas-confetti";
import Card from '../experience/ExperienceCard.jsx';
import info from '../../data/info.js';
import Pdf from "../documents/resume.pdf";
import IconExternalOpen from '../icons/externalopen';
import CAT from "../../images/cat.png";
import CAT_PET from "../../images/cat_pet.png";
import CAT_MOUSE from "../../images/cat_mouse.png";
import CAT_CLICK from "../../images/cat_click.png";
import { useState } from 'react';


const experienceData = [
  {
    period: "2024-Present",
    title: "Co-Op Designer 路 JP Thomson Architects Ltd",
    description: "Developing company website, admin portal,and management system for easy and flexible user-based management of company information. Managing social media accounts and content creation, leading to a 50% increase in engagement within one month.",
    skills: ["HTML", "CSS", "JavaScript", "C#", "MSQL", "Docker", "GitHub"]
  },
  {
    period: "2024-Present",
    title: "Research Assistant 路 GenAI Impacts @ ISTEP, Univeristy of Toronto",
    description: "Developing frameworks for the ethical implementation of GenAI in engineering education, supervised by Dr Qin Liu and Professor Greg Evans. Methodology includes usability testing of GenAI models (focused on multi-modal and chain of thought prompting) and analysis of LLM architecture"
  }
];

const ConfettiContext = createContext({})

const Confetti = forwardRef((props, ref) => {
  const {
    options,
    globalOptions = { resize: true, useWorker: true },
    manualstart = false,
    children,
    ...rest
  } = props
  const instanceRef = useRef(null) // confetti instance

  const canvasRef = useCallback(
    // https://react.dev/reference/react-dom/components/common#ref-callback
    // https://reactjs.org/docs/refs-and-the-dom.html#callback-refs
    node => {
      if (node !== null) {
        // <canvas> is mounted => create the confetti instance
        if (instanceRef.current) return // if not already created
        instanceRef.current = confetti.create(node, {
          ...globalOptions,
          resize: true
        })
      } else {
        // <canvas> is unmounted => reset and destroy instanceRef
        if (instanceRef.current) {
          instanceRef.current.reset()
          instanceRef.current = null
        }
      }
    },
    [globalOptions]
  )

  // `fire` is a function that calls the instance() with `opts` merged with `options`
  const fire = useCallback(
    (opts = {}) => instanceRef.current?.({ ...options, ...opts }),
    [options]
  )

  const api = useMemo(
    () => ({
      fire
    }),
    [fire]
  )

  useImperativeHandle(ref, () => api, [api])

  useEffect(() => {
    if (!manualstart) {
      fire()
    }
  }, [manualstart, fire])

  return (
    <ConfettiContext.Provider value={api}>
      <canvas ref={canvasRef} {...rest} />
      {children}
    </ConfettiContext.Provider>
  )
})


function Experience() {
  const [isHovered, setIsHovered] = useState(false);
  const [isIn, setIsIn] = useState(false);

  const handleMouseEnter = () => {
    setIsHovered(true);
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
  };

  const handleMouseIn = () => {
    setIsIn(true);
  };

  const handleMouseOut = () => {
    setIsIn(false);
  };

  const handleClick = () => {
    const defaults = {
      spread: 360,
      ticks: 50,
      gravity: 0,
      decay: 0.94,
      startVelocity: 30,
      colors: ["#FFE400", "#FFBD00", "#E89400", "#FFCA6C", "#FDFFB8"],
    };

    const shoot = () => {
      confetti({
        ...defaults,
        particleCount: 40,
        scalar: 1.2,
        shapes: ["star"],
      });

      confetti({
        ...defaults,
        particleCount: 10,
        scalar: 0.75,
        shapes: ["circle"],
      });
    };

    setTimeout(shoot, 0);
    setTimeout(shoot, 100);
    setTimeout(shoot, 200);
  };

  return (
      <div className="flex flex-col pt-20 pb-24  w-full max-md:px-5 text-left">
          <h2 className="text-4xl font-['Inter-ExtraBold'] text-black max-md:max-w-fit">
              Experience
          </h2>
          <div className="flex flex-row">
            <div className="flex flex-col mt-10 w-[65%] max-md:max-w-fit">
                {info.experience.map((item, index) => (
                  <Card 
                    key={index}
                    period={item.period}
                    title={item.title}
                    description={item.description}
                    skills={item.skills}
                    number={item.number}
                  />
                ))}
            </div>
            <div className="translate-x-10  translate-y-10 justify-between flex flex-col w-[25%] max-md:max-w-fit">
                <div onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
                  {isHovered ? (
                    <img src={CAT_PET} alt="cat pet" className="max-w-[85%] cursor-grab object-scale-down"/>
                  ) : (
                    <img src={CAT} alt="cat" className="max-w-[85%] object-scale-down"/>
                  )}
                </div>
                <div className=" -translate-x-20 w-fit h-fit" onClick={handleClick} onMouseEnter={handleMouseIn} onMouseLeave={handleMouseOut}>
                  {isIn ? (
                    <img src={CAT_CLICK} alt="cat pet" className="max-w-[85%] cursor-grab object-scale-down"/>
                  ) : (
                    <img src={CAT_MOUSE} alt="cat" className="max-w-[85%] object-scale-down"/>
                  )}
                </div>
            </div>
          </div>

          <a href={Pdf} target='_blank' rel='noopener noreferrer' className="font-['Myfont'] text-3xl relative inline-flex items-baseline group w-fit text-black font-medium before:content-[''] before:bg-[#FFE68C] before:absolute before:w-[0%] before:h-3 before:z-[-1] before:transition-all before:duration-[0.3s] before:ease-[ease-in-out] before:left-0 before:bottom-2 hover:before:w-full hover:before:bottom-2">
            <span className="relative z-10">
              <div className="inline-flex gap-2 items-center">
                View full resume
                <span className="transition-transform delay-200 group-hover:-translate-y-1 group-hover:translate-x-1 group-focus-visible:-translate-y-1 group-focus-visible:translate-x-1 motion-reduce:transition-none translate-y-px">
                  <IconExternalOpen/>
                </span>
              </div>
            </span>
          </a>
      </div>
    );
}

export default Experience;
