import React from 'react';


const ProjectCard = ({ title, description, technologies, imagePosition }) => {
  return (
    <div className="flex overflow-hidden gap-2.5 items-start  max-w-full">
        <div className="flex overflow-hidden flex-wrap flex-1 shrink gap-2.5 w-full basis-0 min-w-[240px] max-md:max-w-full">
            {imagePosition === 'left' ? (
                <>
                    <div className="flex overflow-hidden flex-col flex-1 shrink px-5 basis-0 min-w-[240px] max-md:max-w-full">
                        <div className="flex flex-1 w-full bg-fuchsia-100 min-h-[185px] max-md:max-w-full" />
                    </div>
                    <ProjectDetails title={title} description={description} technologies={technologies} />
                </>
            ) : (
                <>
                    <ProjectDetails title={title} description={description} technologies={technologies} />
                    <div className="flex overflow-hidden flex-col flex-1 shrink px-5 basis-0 min-w-[240px] max-md:max-w-full">
                        <div className="flex flex-1 w-full bg-fuchsia-100 min-h-[185px] max-md:max-w-full" />
                    </div>
                </>
            )}
        </div>
    </div>
  );
};

const ProjectDetails = ({ title, description, technologies }) => {
    return (
        <div className="flex overflow-hidden flex-col flex-1 shrink px-5 text-left my-auto text-sm basis-0 min-w-[240px] max-md:max-w-full">
            <h2 className="text-base font-medium text-black max-md:max-w-full">{title}</h2>
            <p className="mt-3.5 text-black max-md:max-w-full">{description}</p>
            <div className="flex overflow-hidden flex-wrap gap-2.5 items-start mt-3.5 w-full whitespace-nowrap max-md:max-w-full">
                {technologies.map((tech, index) => (
                    <span key={index} className="flex overflow-hidden gap-2.5 justify-center items-center px-4 py-1.5 bg-zinc-100 rounded-[30px]">
                    <span className="self-stretch my-auto bg-clip-text">{tech}</span>
                    </span>
                ))}
            </div>
        </div>
    );
};

export default ProjectCard;
