import React from 'react';
import SkillTag from './SkillTag';

function Card({ period, title, description, skills }) {

    return (
        <div className="flex flex-row   items-start pb-12 w-full max-md:max-w-full">
            <div className="">
                <p className="text-base text-zinc-400 ">{period}</p>
            </div>
            <div className="flex flex-col text-sm w-[70%]">
                <h3 className="text-base font-bold text-black  max-md:max-w-full">
                    {title}
                </h3>
                <p className="mt-3.5 text-sm leading-6 text-zinc-500 max-md:max-w-full">
                    {description}
                </p>
                {   skills && (
                <div className="flex text-xs font-medium overflow-hidden flex-wrap gap-2.5 items-start mt-3.5 w-full whitespace-nowrap max-md:max-w-full">
                    {skills.map((skill, index) => (
                    <SkillTag key={index} skill={skill} />
                    ))}
                </div>
                )}
            </div>
        </div>
    );
}

export default Card;
