import React from 'react';

const MouseTracker = ({ children, offset = { x: 0, y: 0} }) => {    const element = useRef({});    useDocumentEvent('mousemove', (e) => {        if (element.current) {            const x = e.clientX + offset.x, y = e.clientY + offset.y;            element.current.style.transform = `translate(${x}px, ${y}px)`;            element.current.style.visibility = 'visible';        }    });
    return createPortal(        <div className='mouse-tracker' ref={element}>            {children}        </div>    , document.body);};

const Profile = () => {
    return (
        <div className="flex overflow-hidden flex-col justify-center px-24 py-60 w-full max-md:px-5 w-full text-left">
            <div className="flex flex-col text-left items-start gap-3 pt-3.5 pr-16 pb-16  max-w-full w-fit max-md:pr-5">
                <a href=''>
                    <h1 className="text-6xl font-['Myfont'] font-regular text-black max-md:max-w-full ">
                    Hello there!
                    </h1>    
                </a>
                <MouseTracker>
                    <iframe width="560" height="315" src="https://www.youtube.com/embed/rEq1Z0bjdwc?autoplay=1" allowfullscreen></iframe>
                </MouseTracker>


                <p className="text-7xl font-['Inter-ExtraBold'] leading max-md:max-w-full max-md:text-4xl ">
                    I'm&nbsp;
                    <span className="relative inline-flex items-baseline group w-fit before:content-[''] before:bg-[#FFE68C] before:absolute before:w-[0%] before:h-6 before:z-[-1] before:transition-all before:duration-[0.3s] before:ease-[ease-in-out] before:left-0 before:bottom-6 hover:before:w-full hover:before:bottom-6">
                        Katherine Ye
                    </span>
                </p>
                <p className="w-[50%] mt-10 text-xl leading-8 font-normal text-[color:var(--sds-color-text-default-default)] max-md:mt-10 max-md:max-w-[70%]">
                    I'm a designer and developer studying Computer Engineering at the University of Toronto, dedicated to building innovative integrated solutions and continuously exploring the ever-growing field of technology.
                </p>
                {/*
                <div className="flex items-start mt-0">
                    <div className="flex z-10 shrink-0 mr-0 max-w-full bg-yellow-400 bg-opacity-50 h-[18px] w-[378px]"/>
                    <div className="flex z-10 shrink-0 mt-8 mr-0 max-w-full bg-yellow-400 bg-opacity-50 h-[18px] w-[392px]"/>
                </div>
                */}
            </div>
        </div>
    );
};

const Hero = () => {
  return (
    <div className="flex overflow-hidden flex-col h-dvh">
      <Profile />
    </div>
  );
};

export default Hero;
