import React, { useState, useEffect } from 'react';
import Socials from '../cards/Socials'

const navLinks = [
    { title: 'HOME', url: '/' },
    { title: 'GALLERY', url: '/gallery' },
    { title: 'PROJECTS', url: '/projects' }
];

const bgColor = 'bg-gray-800';
const modalColor = 'bg-gray-900';

const Navbar = () => {
    const [isMobile, setIsMobile] = useState(window.innerWidth < 769);
    const [showModal, setShowModal] = useState(false);

    useEffect(() => {
        const handleResize = () => {
            setIsMobile(window.innerWidth < 769);
        };

        window.addEventListener('resize', handleResize);

        return () => {
            window.removeEventListener('resize', handleResize);
        };
    }, []);

    const toggleModal = () => {
        setShowModal(!showModal);
    };

    const handleBarsIconClick = () => {
        toggleModal();
    };

    return (
        <>  <div className="flex flex-row -translate-x-72 w-[full] h-[110%] transition ease-in-out duration-300 hover:-translate-x-40 hover:rotate-[2deg]">
                <div className="flex flex-col justify-between w-full min-w-80 shadow items-end h-svh py-16 px-12 ">
                    <ul className="flex flex-col gap-4 md:gap-4 text-left cursor-pointer">
                        {navLinks.map((link, index) => (
                            <li key={index} className="text-black font-['MyFont']  pl-20  text-2xl">{link.title}</li>
                        ))}
                    </ul>
                    <Socials/>
                </div>
                <div className="mt-10 flex flex-col bg-white shadow w-10 h-32 cursor-pointer"></div>
            </div>
        </>
    );
}

export default Navbar;
